export type AlignmentOption = 'start' | 'center' | 'end' | number

type Params = {
  viewSize: number
  align: AlignmentOption
}

export type Alignment = {
  measure: (n: number) => number
}

export function Alignment(params: Params): Alignment {
  const { viewSize, align } = params
  const predefined = { start, center, end }

  function start(): number {
    return 0
  }

  function center(n: number): number {
    return end(n) / 2
  }

  function end(n: number): number {
    return viewSize - n
  }

  function percent(): number {
    return viewSize * Number(align)
  }

  function measure(n: number): number {
    if (typeof align === 'number') return percent()
    return predefined[align](n)
  }

  const self: Alignment = {
    measure,
  }
  return self
}

sources.src/vanilla/components/animation.ts:
type Callback = () => void

export type Animation = {
  start: () => void
  stop: () => void
  proceed: () => void
}

export function Animation(callback: FrameRequestCallback): Animation {
  let animationFrame = 0

  function ifAnimating(active: boolean, cb: Callback): Callback {
    return (): void => {
      if (active === !!animationFrame) cb()
    }
  }

  function start(): void {
    animationFrame = window.requestAnimationFrame(callback)
  }

  function stop(): void {
    window.cancelAnimationFrame(animationFrame)
    animationFrame = 0
  }

  const self: Animation = {
    proceed: ifAnimating(true, start),
    start: ifAnimating(false, start),
    stop: ifAnimating(true, stop),
  }
  return self
}

sources.src/vanilla/components/axis.ts:
export type AxisOption = 'x' | 'y'

export type Axis = {
  cross: AxisOption
  scroll: AxisOption
  measure: (node: HTMLElement) => number
}

export function Axis(axis: AxisOption): Axis {
  const scroll = axis === 'y' ? 'y' : 'x'
  const cross = axis === 'y' ? 'x' : 'y'

  function measure(node: HTMLElement): number {
    const { width, height } = node.getBoundingClientRect()
    return scroll === 'x' ? width : height
  }

  const self: Axis = {
    cross,
    measure,
    scroll,
  }
  return self
}

sources.src/vanilla/components/utils.ts:
export function map(
  value: number,
  iStart: number,
  iStop: number,
  oStart: number,
  oStop: number,
): number {
  return oStart + (oStop - oStart) * ((value - iStart) / (iStop - iStart))
}

export function mathSign(n: number): number {
  return !n ? 0 : n / Math.abs(n)
}

export function deltaAbs(valueB: number, valueA: number): number {
  return Math.abs(valueB - valueA)
}

export function factorAbs(valueB: number, valueA: number): number {
  if (valueB === 0 || valueA === 0) return 0
  if (Math.abs(valueB) <= Math.abs(valueA)) return 0
  const diff = deltaAbs(Math.abs(valueB), Math.abs(valueA))
  return Math.abs(diff / valueB)
}

export function roundToDecimals(decimalPoints: number): (n: number) => number {
  const pow = Math.pow(10, decimalPoints)
  return (n: number): number => Math.round(n * pow) / pow
}

export function debounce(callback: () => void, time: number): () => void {
  let timeout = 0
  return (): void => {
    window.clearTimeout(timeout)
    timeout = window.setTimeout(callback, time) || 0
  }
}

export function groupArray<GenericType>(
  array: GenericType[],
  size: number,
): GenericType[][] {
  const groups = []
  for (let i = 0; i < array.length; i += size) {
    groups.push(array.slice(i, i + size))
  }
  return groups
}

export function arrayKeys<GenericType>(array: GenericType): number[] {
  return Object.keys(array).map(Number)
}

export function removeClass(node: HTMLElement, className: string): void {
  const cl = node.classList
  if (className && cl.contains(className)) cl.remove(className)
}

export function addClass(node: HTMLElement, className: string): void {
  const cl = node.classList
  if (className && !cl.contains(className)) cl.add(className)
}

